INSERT INTO community (id, user_id, title, content, view_count, is_deleted, created_at, updated_at) VALUES
(1, 4, '인형뽑기 꿀팁 공유합니다!', '안녕하세요! 인형뽑기 5년차입니다. 제가 그동안 터득한 꿀팁들을 공유해볼게요. 첫째, 기계의 힘을 먼저 파악하세요. 둘째, 인형의 위치를 잘 살펴보세요. 셋째, 포인트를 정확히 노리세요!', 245, false, '2026-01-01 09:30:00', '2026-01-01 09:30:00'),
(2, 5, '강남 인형뽑기방 추천해요', '강남역 근처에 새로 생긴 인형뽑기방 다녀왔는데 정말 좋더라구요! 기계도 신형이고 인형도 다양해요. 특히 산리오 캐릭터가 많아서 좋았어요. 주말에 가시면 사람이 많으니 평일 오후가 좋아요.', 189, false, '2026-01-02 14:20:00', '2026-01-02 14:20:00'),
(3, 6, '오늘의 전리품 자랑~', '오늘 홍대에서 인형 5개 뽑았어요!! 정말 운이 좋았던 날이네요. 특히 큰 토끼 인형은 한번에 성공했어요. 사진 첨부할게요!', 456, false, '2026-01-02 18:45:00', '2026-01-02 18:45:00'),
(4, 7, '인형뽑기 기계 종류별 공략법', '기계 종류에 따라 공략법이 다른데요, 2단 집게는 측면 공략이 유리하고, 3단 집게는 정면 공략이 좋아요. 각 기계별 특성을 알려드릴게요.', 312, false, '2026-01-03 10:15:00', '2026-01-03 10:15:00'),
(5, 8, '신규 오픈 매장 정보', '이번주 신촌에 인형뽑기방 새로 오픈했어요! 오픈 이벤트로 첫 플레이 무료래요. 저도 내일 가볼 예정입니다.', 523, false, '2026-01-03 16:00:00', '2026-01-03 16:00:00'),
(6, 9, '초보자 질문있어요!', '인형뽑기 처음 해보는데요, 어떤 인형이 뽑기 쉬운가요? 작은 인형부터 시작하는게 좋을까요?', 198, false, '2026-01-04 11:30:00', '2026-01-04 11:30:00'),
(7, 10, '명동 인형샵 후기', '명동 지하상가에 있는 인형뽑기방 다녀왔어요. 가격은 좀 비싼데 인형 퀄리티가 좋더라구요. 관광객들도 많았어요.', 267, false, '2026-01-04 15:20:00', '2026-01-04 15:20:00'),
(8, 11, '대박 찬스 놓쳤어요 ㅠㅠ', '인형이 거의 떨어질뻔 했는데 마지막에 놓쳤어요... 너무 아쉽네요. 한번만 더 도전할걸 그랬나봐요.', 423, false, '2026-01-05 12:45:00', '2026-01-05 12:45:00'),
(9, 12, '산리오 캐릭터 전문점 발견!', '잠실에 산리오 캐릭터 인형만 있는 곳 찾았어요! 시나모롤, 쿠로미 등 다양해요. 산리오 좋아하시는 분들 강추!', 678, false, '2026-01-05 17:30:00', '2026-01-05 17:30:00'),
(10, 13, '인형뽑기 월 예산은?', '여러분은 인형뽑기에 한달에 얼마 정도 쓰시나요? 저는 10만원 정도 쓰는데 많이 쓰는건가요?', 345, false, '2026-01-06 09:00:00', '2026-01-06 09:00:00'),
(11, 14, '집게 힘 약한 매장 신고', 'XX역 근처 매장인데 집게 힘이 너무 약해요. 거의 사기 수준... 여러분 조심하세요!', 892, false, '2026-01-06 13:15:00', '2026-01-06 13:15:00'),
(12, 15, '인형 보관 어떻게 하세요?', '인형을 너무 많이 뽑아서 보관할 곳이 없어요. 여러분은 어떻게 보관하시나요? 좋은 방법 있으면 알려주세요!', 234, false, '2026-01-07 10:30:00', '2026-01-07 10:30:00'),
(13, 16, '친구 생일선물로 인형뽑기', '친구 생일선물로 인형 뽑아줬는데 완전 좋아하더라구요! 직접 뽑은 인형이라 더 의미있대요.', 456, false, '2026-01-07 14:50:00', '2026-01-07 14:50:00'),
(14, 17, '이번주 신상 인형 정보', '이번주에 디즈니 신상 인형들이 들어온다고 해요! 겨울왕국 캐릭터들이 대거 입고된대요.', 567, false, '2026-01-08 09:20:00', '2026-01-08 09:20:00'),
(15, 18, '인형뽑기 유튜버 추천', '인형뽑기 잘하는 유튜버들 추천해주세요! 공략법 배우고 싶어요.', 389, false, '2026-01-08 16:00:00', '2026-01-08 16:00:00'),
(16, 19, '커플 데이트 코스로 좋아요', '연인과 인형뽑기 데이트 다녀왔는데 너무 재밌었어요! 뽑아준 인형 받고 엄청 좋아했어요.', 512, false, '2026-01-09 11:00:00', '2026-01-09 11:00:00'),
(17, 20, '밤늦게까지 하는 곳 있나요?', '야간 알바 끝나고 하고 싶은데 밤늦게까지 하는 곳 아시는 분?', 198, false, '2026-01-09 22:30:00', '2026-01-09 22:30:00'),
(18, 21, '오락실 vs 전문샵 어디가 나은가요?', '오락실 안에 있는 인형뽑기 vs 전문 인형뽑기방, 어디가 더 나은가요? 장단점이 궁금해요.', 423, false, '2026-01-10 10:15:00', '2026-01-10 10:15:00'),
(19, 22, '대형 인형 뽑기 성공!', '드디어 대형 인형 뽑기 성공했어요! 5만원은 쓴 것 같은데... 그래도 성공해서 기분 좋네요!', 734, false, '2026-01-10 15:40:00', '2026-01-10 15:40:00'),
(20, 23, '주말 핫플 추천', '이번 주말에 인형뽑기 가려고 하는데 어디가 좋을까요? 신촌 vs 홍대 고민중이에요.', 267, false, '2026-01-11 09:00:00', '2026-01-11 09:00:00'),
(21, 24, '인형뽑기 중독인가요?', '요즘 매일 인형뽑기 생각만 해요... 이거 중독인가요? ㅋㅋㅋ 같은 분 계신가요?', 489, false, '2026-01-11 13:20:00', '2026-01-11 13:20:00'),
(22, 25, '포켓몬 인형 파는 곳', '포켓몬 인형 뽑을 수 있는 곳 있나요? 피카츄 인형 너무 갖고 싶어요!', 356, false, '2026-01-11 17:00:00', '2026-01-11 17:00:00'),
(23, 26, '가성비 최고 매장 발견!', '건대입구역 근처에 가성비 최고인 곳 찾았어요. 1회 1000원인데 기계 힘도 좋아요!', 823, false, '2026-01-12 10:30:00', '2026-01-12 10:30:00'),
(24, 27, '비오는 날 인형뽑기 데이트', '비오는 날 할거 없어서 인형뽑기 갔는데 의외로 낭만적이더라구요. 실내 데이트 추천!', 412, false, '2026-01-12 14:00:00', '2026-01-12 14:00:00'),
(25, 28, '마블 캐릭터 인형 어디서?', '마블 히어로 인형들 뽑을 수 있는 곳 있나요? 아이언맨, 스파이더맨 같은거요!', 298, false, '2026-01-12 18:30:00', '2026-01-12 18:30:00'),
(26, 29, '인형뽑기 실력 늘리는 법', '처음에는 못했는데 지금은 제법 잘 뽑아요! 실력 늘리는 꿀팁 공유할게요.', 645, false, '2026-01-13 09:00:00', '2026-01-13 09:00:00'),
(27, 30, '어린이와 함께 가기 좋은 곳', '조카랑 같이 갈만한 곳 추천해주세요! 아이들이 좋아할만한 캐릭터 많은 곳이요.', 234, false, '2026-01-13 11:30:00', '2026-01-13 11:30:00'),
(28, 4, '오늘 신기록 달성!', '오늘 10번 만에 인형 6개 뽑았어요! 제 인생 최고 기록입니다 ㅎㅎ', 567, false, '2026-01-13 14:00:00', '2026-01-13 14:00:00'),
(29, 5, '인형뽑기 대회 있나요?', '인형뽑기 실력 대회 같은거 있나요? 참가해보고 싶어요!', 389, false, '2026-01-13 16:20:00', '2026-01-13 16:20:00'),
(30, 6, '올해 목표: 인형 100개 뽑기!', '올해 목표를 인형 100개 뽑기로 정했어요! 지금까지 15개 뽑았으니 화이팅!', 478, false, '2026-01-13 19:00:00', '2026-01-13 19:00:00');
